using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using UnityEngine.UI;

public class HRscript : MonoBehaviour
{
    [SerializeField] private Text displayText; // Reference to UI Text component to display current value
    [SerializeField] private Transform valueIndicator; // Transform that will be positioned to show current value
    [SerializeField] private float maxHeight = 10f; // Maximum height for visualization
    [SerializeField] private float baseY = 0f; // Base Y position

    [Header("Update Settings")]
    [Range(1, 120)]
    [SerializeField] private float updatesPerSecond = 60f; // Range from 1 to 120 FPS
    [SerializeField] private bool updateEveryFrame = false; // Option to update every frame regardless of rate

    [Header("Monitoring")]
    [SerializeField] private float currentValue; // Exposed to monitor current value in Inspector

    private int[] rawData;
    private List<int> filteredData = new List<int>();
    private int currentIndex = 0;
    private bool isPlaying = false;
    private float maxValue;
    private float timeSinceLastUpdate = 0f;
    private float updateInterval => 1f / updatesPerSecond;

    void Start()
    {
        InitializeData();
        FilterData();
        CalculateMaxValue();
        PlayVisualization();
    }

    void Update()
    {
        // Only process if we're playing
        if (!isPlaying || filteredData.Count == 0) return;

        // Update based on selected method
        if (updateEveryFrame)
        {
            UpdateVisualization();
        }
        else
        {
            // Time-based update at specified rate
            timeSinceLastUpdate += Time.deltaTime;
            if (timeSinceLastUpdate >= updateInterval)
            {
                UpdateVisualization();
                timeSinceLastUpdate = 0f;
            }
        }
    }

    private void UpdateVisualization()
    {
        // Update the exposed monitoring field
        currentValue = filteredData[currentIndex];

        // Update text display
        if (displayText != null)
        {
            displayText.text = $"Value: {currentValue}";
        }

        // Update position of value indicator
        if (valueIndicator != null)
        {
            float normalizedHeight = currentValue / maxValue;
            float newY = baseY + (normalizedHeight * maxHeight);
            Vector3 newPosition = valueIndicator.position;
            newPosition.y = newY;
            valueIndicator.position = newPosition;
        }

        // Move to next index, with wrap-around
        currentIndex = (currentIndex + 1) % filteredData.Count;
    }

