    private void FilterData()
    {
        // Filter out -1 values
        foreach (int value in rawData)
        {
            if (value != -1)
            {
                filteredData.Add(value);
            }
        }
        Debug.Log($"Filtered data contains {filteredData.Count} values");
    }

    private void CalculateMaxValue()
    {
        maxValue = 0;
        foreach (int value in filteredData)
        {
            if (value > maxValue)
            {
                maxValue = value;
            }
        }
        Debug.Log($"Max value found: {maxValue}");
    }

    // Control methods that can be called from buttons or other scripts
    public void PlayVisualization()
    {
        isPlaying = true;
        timeSinceLastUpdate = 0f;
    }

    public void PauseVisualization()
    {
        isPlaying = false;
    }

    public void RestartVisualization()
    {
        currentIndex = 0;
        timeSinceLastUpdate = 0f;
        isPlaying = true;
    }

    // Option to manually move to a specific index
    public void JumpToIndex(int index)
    {
        if (index >= 0 && index < filteredData.Count)
        {
            currentIndex = index;
            timeSinceLastUpdate = 0f;

            // Update visualization immediately
            UpdateVisualization();
        }
    }
}